import React, {
  Fragment
} from 'react';
import PropTypes from 'prop-types';
import {
  Link
} from 'react-router-dom';
// import Moment from 'react-moment';
import {
  connect
} from 'react-redux';

const PostItem = ({
  auth,
  post: {
    _id,
    story,
    name,
    avatar,
    user,
    likes,
    comments,
    date
  }
}) => ( <
  div className = 'post bg-white p-1 my-1' >
  <
  div >
  <
  Link to = {
    `/profile/${user}`
  } >
  <
  img className = 'round-img'
  src = {
    avatar
  }
  alt = '' / >
  <
  h4 > {
    name
  } < /h4> <
  /Link> <
  /div> <
  div >
  <
  p className = 'my-1' > {
    story
  } < /p> <
  p className = 'post-date' > {
    /* Posted on <Moment format='YYYY/MM/DD'>{date}</Moment> */ } <
  /p> <
  button type = 'button'
  class = 'btn btn-light' >
  <
  i class = 'fas fa-thumbs-up' / >
  <
  span > {
    likes.length
  } < /span> <
  /button> <
  button type = 'button'
  class = 'btn btn-light' >
  <
  i class = 'fas fa-thumbs-down' / >
  <
  /button> <
  a href = 'post.html'
  class = 'btn btn-primary' >
  Add to the story < span class = 'comment-count' > {
    comments.length
  } < /span> <
  /a> <
  button type = 'button'
  class = 'btn btn-danger' >
  <
  i class = 'fas fa-times' / >
  <
  /button> <
  /div> <
  /div>
);

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps, {}
)(PostItem);
