import React from 'react';
import PropTypes from 'prop-types';

const ProfileItem = ({
    profile: {
      user: {
        _id,
        name,
        avatar
      },
      location,
      position
    }
  }) => {
    return ( <
        div className = 'profile bg-light' >
        <
        img src = {
          avatar
        }
        alt = ''
        className = 'round-img' / >
        <
        div >
        <
        h2 > {
          name
        } < /h2> <
        p className = 'my-1' > {
          location && < span > {
            location
          } < /span>}</p >
          <
          p className = 'my-1' > {
            position && < span > {
              position
            } < /span>}</p >
            <
            /div> <
            /div>
          );
        };

        ProfileItem.propTypes = {
          profile: PropTypes.object.isRequired
        };

        export default ProfileItem;
